package ru.cpsmi.spring.web;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import ru.cpsmi.spring.model.Person;import ru.cpsmi.spring.service.StarWarsPersonService;@Controllerpublic class MainController {    @Autowired    private StarWarsPersonService service;    @RequestMapping(value = "/{id}", method = RequestMethod.GET)    public String root(ModelMap model, @PathVariable(name = "id") String id) {        Person person = service.getPerson(id);        model.put("person", person);        return "page";    }}